package com.sim.baselibrary.utils;import android.content.Context;/** * @author Sim --- dp、sp工具类 */public class DensityUtil {    /**     * 根据手机的分辨率从 dp 的单位 转成为 px(像素)     *     * @param paramContext     * @param paramFloat     * @return     */    public static int dp2px(Context paramContext, float paramFloat) {        return (int) (0.5F + paramFloat * paramContext.getResources().getDisplayMetrics().density);    }    /**     * 根据手机的分辨率从 px(像素) 的单位 转成为 dp     *     * @param context     * @param paramFloat     * @return     */    public static int px2dp(Context context, float paramFloat) {        return (int) (0.5F + paramFloat / context.getResources().getDisplayMetrics().density);    }    /**     * 根据手机的分辨率从 sp的单位 转成为 px     *     * @param context     * @param spValue     * @return     */    public static int sp2px(Context context, float spValue) {        final float fontScale = context.getResources().getDisplayMetrics().scaledDensity;        return (int) (spValue * fontScale + 0.5f);    }    /**     * 将px值转换为sp值，保证文字大小不变     *     * @param context     * @param pxValue     * @return     */    public static int px2sp(Context context, float pxValue) {        final float fontScale = context.getResources().getDisplayMetrics().scaledDensity;        return (int) (pxValue / fontScale + 0.5f);    }}  